import React , {useEffect,useState} from 'react'
import {firestore} from '../../firebase/config'
import { FaFileSignature,FaSortAmountUpAlt,FaFeatherAlt,FaBarcode } from 'react-icons/fa'

const Books = () => {
      const [books,setBooks] = useState([]);
      useEffect(()=>{
                  const unsub = firestore.collection('Book').onSnapshot((snap)=>{
                  let books = [];
                  snap.forEach(doc => {
                        books.push({...doc.data()});
                  });
                  setBooks((oldBooks)=>{
                        return [...oldBooks,...books]
                  });
                  return () => unsub();
            });
      },[])


      return (
            <div className='admin-book'>
                  <form className='admin-book-add'>
                        <h3>Add a book</h3>
                        <div className='admin-frm-input'>
                              <FaBarcode/>
                              <input type="text" placeholder='Enter ISBN' />
                        </div>
                        <div className='admin-frm-input'>
                              <FaFileSignature/>
                              <input type="text" placeholder='Enter Bookname'/>
                        </div>
                        <div className='admin-frm-input'>
                              <FaFeatherAlt/>
                              <input type="text" placeholder='Enter Author'/>
                        </div>
                        <div className='admin-frm-input'>
                              <FaSortAmountUpAlt/>
                              <input type="text" placeholder='Enter Category'/>
                        </div>
                        <div className='admin-frm-input'>
                              <FaSortAmountUpAlt/>
                              <input type="text" placeholder='Enter Price'/>
                        </div>
                        <input type="file"/>
                  </form>
                  <table className='admin-book-table'>
                        <thead>
                              <tr>
                                    <th>ISBN</th>
                                    <th>Bookname</th>
                                    <th>Author</th>
                                    <th>Category</th>
                                    <th>Price</th>
                              </tr>
                        </thead>
                        <tbody>
                              {books.map((book)=>{
                              const {ISBN,Bookname,Author,Category,Price} = book;
                              return<tr key={ISBN}>
                                    <td>{ISBN}</td>
                                    <td>{Bookname}</td>
                                    <td>{Author}</td>
                                    <td>{Category}</td>
                                    <td>{Price} $</td>
                              </tr>
                              })}
                        </tbody>
                        
                  </table>
            </div>
      )
}

export default Books
