import React , {useState,useEffect} from 'react'
import {firestore} from '../firebase/config'
import Book from './Book'
import {Link} from 'react-router-dom'

const Header =()=>{
      const [books, setBooks] = useState([])

      

      useEffect(()=>{
                  let i =1;
                  const unsub = firestore.collection('Book').onSnapshot((snap)=>{
                  let books = [];
                  for (const book of snap.docs) {
                        books.push({...book.data()});
                        if(i===3)
                              break;
                        i++;
                  };
                  setBooks(books);
                  return () => unsub();
            });
      },[])


      return(
            <form className='header'>
                  <div className='header-books'>
                        {books.map((book,index)=>{
                              const {Bookname,Author,Description,Price,imgUrl} = book;
                                    return(<Book/>)
                              })}
                  </div>
                  <Link to='/books'><input type='button' value='See more' /></Link>
            </form>
      )
}

export default Header
