import React, { useEffect } from 'react'
import useStorage from '../../firebase/useStorage'

const Progress = ({file,setFile,setUrl})=>{
      const {urll, progress} = useStorage(file);
      useEffect(()=>{
            if(urll){
                  setFile(null)
            }
      },[urll,setFile])
      return(
            <div className='progress-bar' style={{backgroundColor:'greenyellow',width:progress+'%',height:10}}></div>
      )
}

export default Progress;
