import React,{useRef} from 'react';
import {FaPhoneAlt,FaLocationArrow,FaEnvelope, FaExclamationCircle} from 'react-icons/fa'

const Footer =()=>{
      const name = useRef(null);
      const email = useRef(null);
      const message = useRef(null);
      const handleSend = (e) => {
            e.preventDefault();
            window.Email.send({
                  Host: "smtp.gmail.com",
                  Username: "plutorider47@gmail.com",
                  Password :"",
                  To: "plutorider47@gmail.com",
                  From: "plutorider47@gmail.com",
                  Subject: name.current.value + " sent you a message.",
                  Body: "Name : "+name.current.value+"<br/>Email : "+email.current.value+"<br/>Message : "+message.current.value
            })
            
      }

      constructor = (props) => {
            this.state = { feedback: '', name: 'Name', email: 'email@example.com' };
            this.handleChange = this.handleChange.bind(this);
            this.handleSubmit = this.handleSubmit.bind(this);
      }

      return (
            <footer>
                  <div className="footContact">
                        
                        <h2>Contact Us</h2><br/>
                        <h4>We'd ❤️ to help!</h4>
                        <p>We like to create things with fun, open-minded people. Feel free to say hello!</p><br/>
                        <div className="contact_frm">
                              <div className="contact_left"> 
                                    <form>
                                          <input type="text"  placeholder="Your name" ref={name} required/>
                                          <input type="email" placeholder="Email" ref={email}  required/>
                                          <textarea rows="12" placeholder="Message" ref={message}  required></textarea>
                                          <input type="submit" value="Send" onClick={handleSubmit}/>
                                    </form>
                              </div>
                              <div className="contact_right">
                                    <div className='contact_right_div'>
                                          <FaLocationArrow/>
                                          <label>Tetouan, Morocco</label>
                                    </div>
                                    <div className='contact_right_div'>
                                          <FaPhoneAlt/><label>(+212) 6 18 05 39 29</label>
                                    </div>
                                    <div className='contact_right_div'>
                                          <FaEnvelope/><label>plutorider47@gmail.com</label>
                                    </div>
                              </div>
                        </div>
                  </div>
                  <div>
                        <div className="footline"></div>
                        Copyright © 2021 Bookshop
                  </div>
            </footer>
      )
      function handleSubmit () {
            const templateId = 'template_id';
      
            this.sendFeedback(templateId, {message_html: this.state.feedback, from_name: this.state.name, reply_to: this.state.email})
      }
      
      function sendFeedback (templateId, variables) {
            window.emailjs.send(
            'gmail', templateId,
            variables
            ).then(res => {
            console.log('Email successfully sent!')
            })
              // Handle errors here however you like, or use a React error boundary
            .catch(err => console.error('Oh well, you failed. Here some thoughts on the error that occured:', err))
        }
}
export default Footer;
