import React , {useState,useEffect} from 'react'
import Book from './Book'
import Navbar from './Navbar';
import {FaSearch} from 'react-icons/fa';
import {firestore} from '../firebase/config'

const Books = () =>{
      const [load,setLoading] = useState(false); 
      const [books,setBooks] = useState([])

      const handleSearch = (e) =>{
            e.preventDefault();
      }

      useEffect(()=>{
            setLoading(true);
                  const unsub = firestore.collection('Book').onSnapshot((snap)=>{
                  let books = [];
                  snap.forEach(doc => {
                        books.push({...doc.data(),id: doc.id});
                  });
                  setBooks((oldBooks)=>{
                        return [...oldBooks,...books]
                  });
                  setLoading(false);
                  return () => unsub();
            });
      },[])

      useEffect(()=>{
            const event = window.addEventListener('scroll',()=>{
                  if(!load && window.innerHeight + window.scrollY>= document.body.scrollHeight  ){
                        setLoading(true);
                  }
            })
            return ()=> window.removeEventListener('scroll',event);
      },[])

      return(
            <>
                  <Navbar/>
                  <div style={{height:'72px'}}></div>
                  <section className='search'>
                        <form className='search-form'>
                              <input type='text' placeholder='Search' className='form-input'/>
                              <button type='submit' className='submit-btn' onClick={(e)=>handleSearch(e)} ><FaSearch/></button>
                        </form>
                  </section>
                  <section className='books'>
                        <div className='books-center'>
                        {books.map((book,index)=>{
                              return <Book key={index} {...book}/>
                        })}
                        </div>
                        {load && <h2 className="loading">Loading...</h2>}
                  </section>
            </>
      )
}

export default Books;
