import React , {useEffect,useState} from 'react'
import {storage,firestore} from '../../firebase/config'
import { FaMoneyBillWave,FaFileSignature,FaSortAmountUpAlt,FaFeatherAlt,FaBarcode, FaImage,FaLayerGroup } from 'react-icons/fa'

const Books = () => {
      const types = ['image/png','image/jpeg'];
      const [file,setFile] =  useState('');
      const [url,setUrl]= useState('');
      const [books,setBooks] = useState([]);
      const [err,setErr] = useState('');
      const [data,setData] = useState({
            ISBN: '',
            Bookname : '',
            Author: '',
            Category:'',
            Qte:'',
            Price: '',
            Description:'',
            imgUrl:''
      })
      

      const handleAdd = (e)=>{
            e.preventDefault();
            while(data.imgUrl ===''){
                  console.log("keeep");
                  setData({...data, imgUrl:url});
            }
            firestore.collection("Book").add(data).then(e.target.reset()).catch((err)=>setErr(err));
      }

      useEffect(()=>{
                  const unsub = firestore.collection('Book').orderBy('Bookname').onSnapshot((snap)=>{
                  let books = [];
                  snap.forEach(doc => {
                        books.push({...doc.data()});
                  });
                  setBooks([...books]);
                  return () => unsub();
            });
      },[])

      const handleDelete = (ISBN) =>{
            firestore.collection('Book').where('ISBN','==',ISBN).get().then(function(querySnapshot) {
                  querySnapshot.forEach(function(doc) {
                  doc.ref.delete();
                  });
            });
      }

      const handleFile = (e) =>{
            let selected = e.target.files[0];

            if(selected && types.includes(selected.type)){
                  setFile(selected);
                  setErr('');
            }else{
                  setFile(null);
                  setErr('Please select an image file (png or jpeg)')
            }
      }

      return (
            <div className='admin-book'>
                  <form className='admin-book-add' onSubmit={handleAdd}>
                        <h4 style={{fontSize:'1.25rem',margin: '1.5rem'}}>Add book</h4>
                        <div className='admin-frm-cont'>
                              <div className='admin-frm-input'>
                                    <FaBarcode/>
                                    <input type="text" placeholder='Enter ISBN' value={data.ISBN} onChange={(e)=>setData({...data, ISBN:e.target.value})}/>
                              </div>
                              <div className='admin-frm-input'>
                                    <FaFileSignature/>
                                    <input type="text" placeholder='Enter Bookname' value={data.Bookname} onChange={(e)=>setData({...data, Bookname:e.target.value})}/>
                              </div>
                              <div className='admin-frm-input'>
                                    <FaFeatherAlt/>
                                    <input type="text" placeholder='Enter Author' value={data.Author} onChange={(e)=>setData({...data, Author:e.target.value})}/>
                              </div>
                              <div className='admin-frm-input'>
                                    <FaSortAmountUpAlt/>
                                    <input type="text" placeholder='Enter Category' value={data.Category} onChange={(e)=>setData({...data, Category:e.target.value})}/>
                              </div>
                              <div className='admin-frm-input'>
                                    <FaLayerGroup/>
                                    <input type="text" placeholder='Enter quantity' value={data.Qte} onChange={(e)=>setData({...data, Qte:e.target.value})}/>
                              </div>
                              <div className='admin-frm-input'>
                                    <FaMoneyBillWave/>
                                    <input type="text" placeholder='Enter Price' value={data.Price} onChange={(e)=>setData({...data, Price:e.target.value})}/>
                              </div>
                              <div style={{alignItems:'center',display:'flex',position:'relative'}}>
                                    <FaImage style={{left:'15px'}}/>
                                    <input style={{width:'90%',margin:'auto',paddingLeft:'40px'}} type="file" accept="image/png, image/jpeg" onChange={handleFile}/>
                              </div> 
                              <textarea placeholder='Enter description' value={data.Description} onChange={(e)=>setData({...data, Description:e.target.value})}>

                              </textarea>
                              
                        </div>
                        <div className='admin-frm-btn'>
                              {err && (<><span style={{color:'red',fontSize:14,margin:5}}>{err}</span><br/></>)}
                              <input type="submit" value="Add"/>
                              <input type="reset" value="Reset"/>
                        </div>
                  </form>
                  <table className='admin-book-table'>
                        <thead>
                              <tr>
                                    <th>ISBN</th>
                                    <th>Bookname</th>
                                    <th>Author</th>
                                    <th>Category</th>
                                    <th>Price</th>
                                    <th>Action</th>
                              </tr>
                        </thead>
                        <tbody>
                              {books.map((book)=>{
                              const {ISBN,Bookname,Author,Category,Price} = book;
                              return<tr key={ISBN}>
                                    <td>{ISBN}</td>
                                    <td>{Bookname}</td>
                                    <td>{Author}</td>
                                    <td>{Category}</td>
                                    <td>{Price} $</td>
                                    <td>
                                          <input type="button" value="Edit"/>
                                          <input type="button" value="Delete" onClick={()=>handleDelete(ISBN)}/>
                                    </td>
                              </tr>
                              })}
                        </tbody>
                        
                  </table>
            </div>
      )
}

export default Books
