import React , {useEffect,useState} from 'react'
import {firestore} from '../../firebase/config'

const Books = () => {
      const [load,setLoading] = useState(false); 
      const [books,setBooks] = useState([])


      useEffect(()=>{
            setLoading(true);
                  const unsub = firestore.collection('Book').onSnapshot((snap)=>{
                  let books = [];
                  snap.forEach(doc => {
                        books.push({...doc.data()});
                  });
                  setBooks((oldBooks)=>{
                        return [...oldBooks,...books]
                  });
                  setLoading(false);
                  return () => unsub();
            });
      },[])


      return (
            <div className='admin-book'>
                  <table>
                        <tr>
                              <th>ISBN</th>
                              <th>Bookname</th>
                              <th>Author</th>
                              <th>Category</th>
                              <th>Price</th>
                              
                        </tr>
                        {books.map((book)=>{
                              const {ISBN,Bookname,Author,Description,Price,imgUrl} = book;
                              return<tr>
                                    <td>{ISBN}</td>
                              </tr>
                        })}
                  </table>
            </div>
      )
}

export default Books
