import React , {useEffect,useState} from 'react'
import {firestore} from '../../firebase/config'
import { FaFileSignature,FaSortAmountUpAlt,FaFeatherAlt,FaBarcode, FaImage } from 'react-icons/fa'

const Books = () => {
      const [data,setData] = useState({
            ISBN: 0,
            Bookname : 'aa',
            Author: '',
            Category:'',
            Price:0,
            Desciprtion:''
      })
      const [books,setBooks] = useState([]);
      const [err,setErr] = useState('');

      const handleAdd =(e)=>{
            e.preventDefault();
            firestore.collection("Book").add(data).catch((err)=>setErr(err));
      }

      useEffect(()=>{
                  const unsub = firestore.collection('Book').onSnapshot((snap)=>{
                  let books = [];
                  snap.forEach(doc => {
                        books.push({...doc.data()});
                  });
                  setBooks((oldBooks)=>{
                        return [...oldBooks,...books]
                  });
                  return () => unsub();
            });
      },[])


      return (
            <div className='admin-book'>
                  <form className='admin-book-add' onSubmit={(e)=>handleAdd(e)}>
                        <h4 style={{fontSize:'1.25rem',margin: '1.5rem'}}>Add book</h4>
                        <div className='admin-frm-cont'>
                              <div className='admin-frm-input'>
                                    <FaBarcode/>
                                    <input type="text" placeholder='Enter ISBN' value={data.ISBN} onChange={setData.ISBN(e.target.value)}/>
                              </div>
                              <div className='admin-frm-input'>
                                    <FaFileSignature/>
                                    <input type="text" placeholder='Enter Bookname'/>
                              </div>
                              <div className='admin-frm-input'>
                                    <FaFeatherAlt/>
                                    <input type="text" placeholder='Enter Author'/>
                              </div>
                              <div className='admin-frm-input'>
                                    <FaSortAmountUpAlt/>
                                    <input type="text" placeholder='Enter Category'/>
                              </div>
                              <div className='admin-frm-input'>
                                    <FaSortAmountUpAlt/>
                                    <input type="text" placeholder='Enter Price'/>
                              </div>
                              <div style={{alignItems:'center',display:'flex',position:'relative'}}>
                                    <FaImage style={{left:'15px'}}/>
                                    <input style={{width:'90%',margin:'auto',paddingLeft:'40px'}} type="file"/>
                              </div> 
                              <textarea placeholder='Enter description'>

                              </textarea>
                              
                        </div>
                        <div className='admin-frm-btn'>
                              {err && (<><span>{err}</span><br/></>)}
                              <input type="submit" value="Add"/>
                              <input type="reset" value="Reset"/>
                        </div>
                  </form>
                  <table className='admin-book-table'>
                        <thead>
                              <tr>
                                    <th>ISBN</th>
                                    <th>Bookname</th>
                                    <th>Author</th>
                                    <th>Category</th>
                                    <th>Price</th>
                                    <th>Action</th>
                              </tr>
                        </thead>
                        <tbody>
                              {books.map((book)=>{
                              const {ISBN,Bookname,Author,Category,Price} = book;
                              return<tr key={ISBN}>
                                    <td>{ISBN}</td>
                                    <td>{Bookname}</td>
                                    <td>{Author}</td>
                                    <td>{Category}</td>
                                    <td>{Price} $</td>
                                    <td>
                                          <input type="button" value="Edit"/>
                                          <input type="button" value="Delete"/>
                                    </td>
                              </tr>
                              })}
                        </tbody>
                        
                  </table>
            </div>
      )
}

export default Books
