import React from 'react';
import About from './About';
import Footer from './Footer';
import Navbar from './Navbar';
import Header from './Header'
import {firestore} from '../firebase/config'

const Home = () => {
      const [info,setInfo] = React.useState({});
      
      React.useEffect(() =>{const unsub = firestore.collection('Home').onSnapshot((snap)=>{
            let info = [];
            snap.forEach(doc => {
                  info.push({...doc.data(),id: doc.id});
            });
            setInfo(info)
            console.log(info);
            return () => unsub();
      })},[]);

      return(
            <>
                  <div style={{height:'72px'}}/>
                  <Header />
                  <div style={{height:'72px'}}/>
                  <Navbar/>
                  <div id='About'/>
                  {info.map((item)=>{
                        
                  return(
                        <React.Fragment>
                              <About {...info[0]}/>
                              <div id='Contact'/>
                              <Footer {...info[0]}/>
                        </React.Fragment>)
                  })}
            </>
      )
}

export default Home ;
