import React from 'react';

const Book =({Bookname,Author,imgUrl})=> {

      return (
      <article className='book'>
            <img src={imgUrl} alt={Bookname}/>
            <div className='book-info'>
                  <h3>{Bookname}</h3>
            </div>
            
            <h4 className='price'>$</h4>
            <h4>{Author}</h4>
      </article>)
}

export default Book;
