import React, {useState,useEffect} from 'react';
import About from './About';
import Footer from './Footer';
import Navbar from './Navbar';
import {firestore} from '../firebase/config'
import {Link} from 'react-router-dom'
import loadPic from '../assets/laod.gif'

const Home = () => {
      const [load,setLoad] = useState(true);
      const [info,setInfo] = useState([]);
      const [books, setBooks] = useState([])

      useEffect(()=>{
                  let i =1;
                  const unsub = firestore.collection('Book').onSnapshot((snap)=>{
                  let books = [];
                  for (const book of snap.docs) {
                        books.push({...book.data()});
                        if(i===3)
                              break;
                        i++;
                  };
                  setBooks(books);
                  return () => unsub();
            });
      },[])

      useEffect(() =>{
            setLoad(true);
            const unsub = firestore.collection('Home').onSnapshot((snap)=>{
            let info = [];
            snap.forEach(doc => {
                  info.push({...doc.data(),id: doc.id});
            });
            setInfo(info)
            setLoad(true);
            return () => unsub();
      })},[]);
      return(<>{load?(<div className='home-loading'><img src='https://miro.medium.com/max/1600/1*i1yv42RrOYhvw7U2MM4qCg.gif' alt='nn'/></div>):(
            <div>
                  <Navbar/>
                  <div style={{height:'72px'}}/>
                  <form className='header'>
                        <div className='header-books'>
                              {books.map((book,index)=>{
                                    const {Bookname,Author,Description,Price,imgUrl} = book;
                                          return(<article key={index} className='book' style={{width:343}}>
                                                      <div className='book-cover'>
                                                            <div className='book-img'>
                                                                  <img src={imgUrl} alt={Bookname}/>
                                                            </div>
                                                      </div>
                                                      <div className='book-info'>
                                                            <h4 className='bookname'>{Bookname}</h4>
                                                            <h4 className='author'>{Author}</h4>
                                                            <p>{Description}</p>
                                                            <h4 className='price'>{Price}$</h4>
                                                      </div>
                                                </article>)
                                    })}
                        </div>
                        <Link to='/books'><input type='button' value='See more' /></Link>
                  </form>               
                  <div id='About'/>
                  {info.map((item,index)=>{
                        const {ContactUsEmail,ContactUsLocation,ContactUsPhoneNumber,OurStoryDescription} = item;
                  return(
                        <React.Fragment key={index}>
                              <About OurStoryDescription={OurStoryDescription}/>
                              <div id='Contact'/>
                              <Footer ContactUsEmail={ContactUsEmail} ContactUsLocation={ContactUsLocation} ContactUsPhoneNumber={ContactUsPhoneNumber}/>
                        </React.Fragment>)
                  })}
            </div>)}</>
      )
}

export default Home ;
