import React, { useState } from 'react'
import { FaBars} from 'react-icons/fa'
import { links,social } from '../data'
import logo from '../logo3.svg'
import {Link} from 'react-router-dom'

const Navbar = () => {
  const [showLinks,setShowLinks] = useState(false);
  const [home,setHome] = useState(true);


  return (
    <nav className='nav'>
      <div className='nav-center'>
        <div className='nav-header'>
          <Link to="/" style={{height: '40px'}}>
            <img src={logo} alt='logo' className='logo'/>         
          </Link>
          <button className='nav-toggle' onClick={()=>{setShowLinks(!showLinks)}}><FaBars /></button>
        </div>
        <div className={`links-container ${showLinks && ' show-container'}`}>
          <ul className='links'>
            {links.map((link)=>{
              return (
              <li key={link.id}>
                {link.text==="contact" ? (home ? (<a href="#Contact">{link.text}</a>):(<Link to='/'>{link.text}</Link>)) : (link.text === 'about' ? (<a href="#About">{link.text}</a>) : (<Link onMouseDown={()=>{setHome(false)}} to={link.url}>{link.text}</Link>))}
                </li>
            )})}
          </ul>
        </div>
        <ul className='social-icons'>
          {social.map((ico)=>{
            return<li key={ico.id}>{ico.id !==4 ? (<a target="_blank" rel="noreferrer" href={ico.url}>{ico.icon}</a>) : (<Link to={ico.url} className="iconLogin">{ico.icon}</Link>) }</li>
          })}
        </ul>
      </div>
    </nav>
  )
}

export default Navbar
