import React , {useEffect,useState} from 'react'
import {firestore} from '../../firebase/config'

const Books = () => {
      const [load,setLoading] = useState(false); 
      const [books,setBooks] = useState([])


      useEffect(()=>{
            setLoading(true);
                  const unsub = firestore.collection('Book').onSnapshot((snap)=>{
                  let books = [];
                  snap.forEach(doc => {
                        books.push({...doc.data()});
                  });
                  setBooks((oldBooks)=>{
                        return [...oldBooks,...books]
                  });
                  setLoading(false);
                  return () => unsub();
            });
      },[])


      return (
            <div className='admin-book'>
                  <form className='admin-book-add'>
                        <input type="text" placeholder='Enter ISBN'/>
                        <input type="text" placeholder='Enter Bookname'/>
                        <input type="text" placeholder='Enter Author'/>
                        <input type="text" placeholder='Enter Category'/>
                        <input type="text" placeholder='Enter Price'/>
                        <input type="file" style={{width:'fit-content'}}/>
                  </form>
                  <table className='admin-book-table'>
                        <thead>
                              <tr>
                                    <th>ISBN</th>
                                    <th>Bookname</th>
                                    <th>Author</th>
                                    <th>Category</th>
                                    <th>Price</th>
                              </tr>
                        </thead>
                        <tbody>
                              {books.map((book)=>{
                              const {ISBN,Bookname,Author,Category,Price} = book;
                              return<tr key={ISBN}>
                                    <td>{ISBN}</td>
                                    <td>{Bookname}</td>
                                    <td>{Author}</td>
                                    <td>{Category}</td>
                                    <td>{Price} $</td>
                              </tr>
                              })}
                        </tbody>
                        
                  </table>
            </div>
      )
}

export default Books
