import React , {useState,useEffect} from 'react'
import {firestore} from '../firebase/config'
import Book from './Book'
import {History} from 'react-router-dom'

const Header =()=>{
      const [books, setBooks] = useState([])

      const handleSeemore = (e) =>{
            e
      }

      useEffect(()=>{
                  let i =1;
                  const unsub = firestore.collection('Book').onSnapshot((snap)=>{
                  let books = [];
                  for (const book of snap.docs) {
                        books.push({...book.data()});
                        if(i===3)
                              break;
                        i++;
                  };
                  setBooks(books);
                  return () => unsub();
            });
      },[])


      return(
            <form className='header' onSubmit={handleSeemore}>
                  <div className='header-books'>
                        {books.map((book,index)=>{
                              const {Bookname,Author,Description,Price,imgUrl} = book;
                                    return(<article className='book'>
                                                <div className='book-cover'>
                                                      <div className='book-img'>
                                                            <img src={imgUrl} alt={Bookname}/>
                                                      </div>
                                                </div>
                                                <div className='book-info'>
                                                      <h4 className='bookname'>{Bookname}</h4>
                                                      <h4 className='author'>{Author}</h4>
                                                      <p>{Description}</p>
                                                      <h4 className='price'>{Price}$</h4>
                                                </div>
                                          </article>)
                              })}
                  </div>
                  <input type='submit' value='See more'/>
            </form>
      )
}

export default Header
