import Header from './Header'

const Home = () => {
      const [info,setInfo] = React.useState({});
      
      React.useEffect(() =>{const unsub = firestore.collection('Book').onSnapshot((snap)=>{
            let books = [];
            snap.forEach(doc => {
                  books.push({...doc.data(),id: doc.id});
            });
            setBooks((oldBooks)=>{
                  return [...oldBooks,...books]
            });
            setLoading(false);
            return () => unsub();
      })});

      return(
            <>
                  <div style={{height:'72px'}}/>
                  <Header />
                  <div style={{height:'72px'}}/>
                  <Navbar/>
                  <div id='About'/>
                  <About/>
                  <div id='Contact'/>
                  <Footer/>
            </>
      )
}

export default Home ;
