import React, {useState,useEffect} from 'react';
import {FaUserAlt,FaLock} from 'react-icons/fa'
import './login.css'
import fire from '../firebase/config';

const Login = () =>{
      const {user,setUser} = useState({});
      const {email,setEmail} = useState('');
      const {pass,setPass} = useState('');
      const {error,setError} = useState('');
      const {hasAccount , setHasAccount} = useState(false); 

      const handleLogin = () =>{
            setError('');
            fire
                  .auth()
                  .signInWithEmailAndPassword(email,pass)
                  .catch((err)=>{
                        setError(err.message);
                  })
      }

      const authListener = ()=>{
            fire.auth().onAuthStateChanged((user) => {
                  if(user){
                        setEmail('');
                        setPass('');
                        setUser(user);
                  }
                  else{
                        setUser('');
                  }
            });
      };

      useEffect(() =>{
            authListener();
      },[]);

      return(
      <div className="align">
            <div className="grid">
                  <form className="form">

                        <div className="form_field">
                        <label htmlFor="user"><FaUserAlt className="icon"></FaUserAlt></label>
                        <input id="user" value={email} onChange={(e)=>{setEmail(e.target.value)}} type="text" className="login_field login_input" placeholder="Email" required/>
                        </div>

                        <div className="form_field">
                        <label htmlFor="pass"><FaLock className="icon"></FaLock></label>
                        <input id='pass' value={pass} onChange={(e)=>{setPass(e.target.value)}} type="password" className="login_field login_input" placeholder="Password" required/>
                        </div>
                        <span>xsx</span>
                        <div className="form_field">
                        <input className="login_btn login_field" type="submit" value="Sign In"/>
                        </div>

                  </form>
            </div>
      </div>
)
}

export default Login;
