import React , {useEffect} from 'react'
import {firestore} from '../firebase/config'

const Books = () => {

      useEffect(()=>{
            setLoading(true);
                  const unsub = firestore.collection('Book').onSnapshot((snap)=>{
                  let books = [];
                  snap.forEach(doc => {
                        books.push({...doc.data(),id: doc.id});
                  });
                  setBooks((oldBooks)=>{
                        return [...oldBooks,...books]
                  });
                  setLoading(false);
                  return () => unsub();
            });
      },[])


      return (
            <div className='admin-book'>
                  <table>
                        <tr>
                              <th>ISBN</th>
                              <th>Bookname</th>
                              <th>Author</th>
                              <th>Category</th>
                              <th>Price</th>
                              
                        </tr>
                  </table>
            </div>
      )
}

export default Books
